    The following example demonstrates how to programmatically create a RadPanelBar
    control. 
    
		void Page_Load(object sender, EventArgs e)
		{
			RadPanelBar myPanelBar = new RadPanelBar();
		}

    The following example demonstrates how to set the SelectedValuePath property 
    of a RadPanelBar.
    
    
		void Page_Load(object sender, EventArgs e)
		{
			panelBar.SelectedValuePath = "Header";
			panelBar.Items.Add(new RadPanelBarItem() { Header = "Item 1" });
		}
		
        public string ExampleMethod()
        {
            // Will return "Item 1"
            return panelBar.SelectedValue.ToString();
        }

    The following example demonstrates how to get the SelectedValue of a RadPanelBar.
    
    
		void Page_Load(object sender, EventArgs e)
		{
			panelBar.SelectedValuePath = "Header";
			panelBar.Items.Add(new RadPanelBarItem() { Header = "Item 1" });
		}
		
        public string ExampleMethod()
        {
            // Will return "Item 1"
            return panelBar.SelectedValue.ToString();
        }

    The following example demonstrates how to get the content of the 
    RadPanelBarItem.Header 
    property of the selected item.
    
    
		void Page_Load(object sender, EventArgs e)
		{
			panelBar.Selected += new System.EventHandler<RadRoutedEventArgs>(panelBar_Selected);
		}
		
        void panelBar_Selected(object sender, RadRoutedEventArgs e)
        {
            RadPanelBarItem selectedItem = e.Source as RadPanelBarItem;
            object itemHeader = selectedItem.Header;
        }

    The following example demonstrates how to prevent the collapsing of a 
    RadPanelBarItem.
    
    
		void Page_Load(object sender, EventArgs e)
		{
			panelBar.PreviewCollapsed += new System.EventHandler<RadRoutedEventArgs>(panelBar_PreviewCollapsed);
		}
		
        void panelBar_PreviewCollapsed(object sender, RadRoutedEventArgs e)
        {
            RadPanelBarItem item = e.Source as RadPanelBarItem;
            
            // Assuming you are looking for a specific header of type String
            if (item.Header.ToString() == "SomeString")
            {
                e.Handled = true;
            }
        }

    The following example demonstrates how to execute an action when a specific  
    RadPanelBarItem is collapsed.
    
    
		void Page_Load(object sender, EventArgs e)
		{
			panelBar.Collapsed += new System.EventHandler<RadRoutedEventArgs>(panelBar_Collapsed);
		}
		
        void panelBar_Collapsed(object sender, RadRoutedEventArgs e)
        {
            RadPanelBarItem item = e.Source as RadPanelBarItem;
            
            // Assuming you are looking for a specific header of type String
            if (item.Header.ToString() == "SomeString")
            {
                // Do some action
            }
        }

